mapper.select:
select  driver_id,
 group_concat(zuob,'') AS route
 from (
 select
 t.driver_id,
 concat('[',t.lng,',',t.lat,']') as  zuob
 from ml_driver_location t
 INNER JOIN ml_user_base b ON b.user_id=t.driver_id
 WHERE   1=1

   AND b.user_name LIKE CONCAT('%', #{name},'%')

 and t.create_time  >=#{strDate}
 AND t.create_time <=#{endDate}
 ) c  group by driver_id

select t.driver_id, concat('[',t.lng,',',t.lat,']') as route from ml_driver_location t INNER JOIN ml_user_base b ON b.user_id=t.driver_id WHERE 1=1
AND b.mobile LIKE CONCAT('%', #{mobile},'%')
and t.create_time >=#{strDate} AND t.create_time <=#{endDate}
