mapper.update:
UPDATE ml_order_pay
SET pay_amount = #{payAmount},pay_currency = #{payCurrency},trade_no = #{tradeNo},
    pay_code = #{payCode},pay_status = #{payStatus},callback_time = #{callbackTime},
    succ_time = #{succTime}
WHERE id = #{id}

mapper.select:
SELECT id
FROM ml_order_pay
WHERE referre_id = #{orderId};
